/**
 * Returns parsed permissions for a given participant.
 * In case no `sessionId` is passed, the hook returns permissions for the local participant.
 * @param sessionId The participant's session_id (optional)
 */
export declare const usePermissions: (sessionId?: string | undefined) => {
    canAdminParticipants: any;
    canAdminStreaming: any;
    canAdminTranscription: any;
    canSendAudio: any;
    canSendCustomAudio: any;
    canSendCustomVideo: any;
    canSendScreenAudio: any;
    canSendScreenVideo: any;
    canSendVideo: any;
    hasPresence: any;
    permissions: any;
};
